mapper.select:
SELECT *
FROM t_coupon_settlement
WHERE coupon_id = #{couponId}
  AND user_id = #{userId}
  AND status in (0, 2)

mapper.update:
UPDATE t_coupon_settlement
SET status = #{freshStatus}
WHERE coupon_id = #{couponId}
AND order_id = #{orderId}
AND user_id = #{userId}
AND status = #{oldStatus}
